import { useState, useEvent } from "react";
import { format } from "date-fns";

function App() {
  const [name, setName] = useState("");
  const [isName, setIsName] = useState(true);
  const [age, setAge] = useState("");
  const [isAge, setIsAge] = useState(true);
  const nameHandler = (event) => {
    const nameValue = event.target.value;
    setName(nameValue);
    if (nameValue.length == 0) {
      setIsName(true);
    } else {
      setIsName(false);
    }
  };
  const ageHandler = (event) => {
    const ageValue = event.target.value;
    setAge(ageValue);
    if (ageValue <= 0 || ageValue >= 100) {
      setIsAge(true);
    } else {
      setIsAge(false);
    }
  };
  const [date, setDate] = useState(new Date());
  const [isDate, setIsDate] = useState(true);

  const dateHandler = (event) => {
    const dateValue = event.target.value;
    setDate(dateValue);
    console.log(new Date().getTime());
    if (new Date().getTime() > dateValue) {
      setIsDate(true);
    } else {
      setIsDate(false);
    }
  };

  return (
    <div>
      <h4>*marked fields are mandatory</h4>
      <div>
        <label>Name:*</label>
        <input
          type="text"
          placeholder="Enter your Name"
          name="Name"
          value={name}
          onChange={nameHandler}
        ></input>
        {isName && <p>*Please enter your name.</p>}
      </div>
      <div>
        <label>Age:*</label>
        <input
          type="number"
          placeholder="Enter your Age between 0 and 100"
          name="Age"
          value={age}
          onChange={ageHandler}
        ></input>
        {isAge && <p>*Please enter the age between 0 and 100</p>}
      </div>
      <div>
        <label>Date of Birth:*</label>
        <input
          type="date"
          min={format(sDate, "DD-MM-YYYY")}
          placeholder="dd/mm/yyyy"
          name="12/03/2021"
          value={date}
          onChange={dateHandler}
        ></input>
        {isDate && <p>*Please enter the valid date of birth</p>}
      </div>
      <div>
        <label>Sum Insured:*</label>

        <input
          type="text"
          placeholder="Enter amount between 0 and 100000"
          name="Name"
          value=""
        ></input>
      </div>
      <div>
        <label>occupation List:*</label>
        <div>
          <select defaultValue={"Select one Occupation"}>
            <option value="Select one Occupation">Select one Occupation</option>
            <option value="Cleaner">Cleaner</option>
            <option value="Doctor">Doctor</option>
            <option value="Engineer">Engineer</option>
          </select>
        </div>
      </div>
    </div>
  );
}

export default App;
